import { useState, useEffect } from "react";
import Navbar from "./Navbar";
import LoginModal from "../login/LoginModal";
import SearchSection from "./SearchSection";
import FeaturesSection from "./FeaturesSection";
import TransactionsTable from "./TransactionsTable";
import axiosInstance from "../utils/axiosInstance"; // Use the Axios instance that handles token refresh

export default function HomePage() {
  const [search, setSearch] = useState("");
  const [isLoginOpen, setIsLoginOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");

  // ✅ Check if user is already logged in when the component mounts
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    const storedUsername = localStorage.getItem("username");

    if (token && storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    } else if (token) {
      fetchUserProfile();
    }
  }, []);

  // ✅ Handle Login and store user details
  const handleLogin = async (user, pass) => {
    try {
      const response = await axiosInstance.post("login/", { username: user, password: pass });

      if (response.status === 200) {
        const { access, refresh } = response.data;

        // Store tokens
        localStorage.setItem("access_token", access);
        localStorage.setItem("refresh_token", refresh);

        setIsLoggedIn(true);
        setIsLoginOpen(false);

        // Fetch user profile after successful login
        fetchUserProfile();
      }
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  // ✅ Fetch the logged-in user's profile
  const fetchUserProfile = async () => {
    try {
      const response = await axiosInstance.get("user-profile/");
      const loggedInUsername = response.data.username;

      // Store username in state and localStorage for persistence
      setUsername(loggedInUsername);
      localStorage.setItem("username", loggedInUsername);
    } catch (error) {
      console.error("Error fetching user profile:", error);
    }
  };

  // ✅ Handle Logout (clear session)
  const handleLogout = () => {
    localStorage.removeItem("access_token");
    localStorage.removeItem("refresh_token");
    localStorage.removeItem("username");

    setIsLoggedIn(false);
    setUsername("");
  };

  return (
    <div className="min-h-screen bg-white text-red-900">
      <Navbar 
        isLoggedIn={isLoggedIn} 
        username={username} 
        handleLogout={handleLogout} 
        handleLoginOpen={() => setIsLoginOpen(true)} 
      />
      <LoginModal 
        isLoginOpen={isLoginOpen} 
        setIsLoginOpen={setIsLoginOpen} 
        handleLogin={handleLogin} 
        setIsLoggedIn={setIsLoggedIn} 
        setUsername={setUsername} 
      />
      <SearchSection search={search} setSearch={setSearch} />
      <FeaturesSection />
      <TransactionsTable />
    </div>
  );
}
