import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { useLocation } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const API_URL = "http://127.0.0.1:8000/api/resale/";

export default function HDBResaleVisualizer() {
  const location = useLocation();
  const {
    selectedDistricts = [],
    selectedRoomType = "",
    type = "multi-district"
  } = location.state || {};

  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedDistricts.length && selectedRoomType) {
      fetchData("price_trends");
    }
  }, [selectedDistricts, selectedRoomType]);

  const fetchData = (analysisType) => {
    setLoading(true);
    setError(null);

    const fetchTownData = (town) => {
      return axios.get(`${API_URL}resale_analysis/`, {
        params: {
          towns: [town],
          room_type: selectedRoomType,
          type: analysisType || "price_trends"
        }
      });
    };

    const fetchPromises = selectedDistricts.map((town) => fetchTownData(town));

    Promise.all(fetchPromises)
      .then((responses) => {
        const data = responses.flatMap((res) => res.data);
        console.log("API Response:", data);

        if (!data.length) {
          setError("No data found for the selected criteria.");
          return;
        }

        // Extract and sort unique years for X-axis
        const uniqueYears = [...new Set(data.map((d) => Number(d.year)))].sort();

        const processedData = {
          labels: uniqueYears,
          datasets: selectedDistricts.map((town) => {
            const townData = data
              .filter((d) => d.town.toUpperCase() === town.toUpperCase())
              .sort((a, b) => Number(a.year) - Number(b.year));

            console.log(`Matching for town: ${town}`);
            console.log("Town data years:", townData.map((d) => d.year));

            return {
              label: town,
              data: uniqueYears.map((year) => {
                const match = townData.find((d) => Number(d.year) === Number(year));
                return match ? parseFloat(match.resale_price) : null;
              }),
              borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
              fill: false,
              tension: 0.1,
            };
          }),
        };

        console.log("Chart Data:", processedData);
        setChartData(processedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError("Failed to fetch data. Please try again.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "HDB Resale Price Trends",
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Year",
        },
        type: "category",
      },
      y: {
        title: {
          display: true,
          text: "Resale Price (SGD)",
        },
        beginAtZero: false,
      },
    },
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold">HDB Resale Price Visualizer</h1>

      {loading && <p className="text-blue-500 mt-4">Loading...</p>}
      {error && <p className="text-red-500 mt-4">{error}</p>}

      {chartData && (
        <div className="mt-6">
          <Line data={chartData} options={chartOptions} />
        </div>
      )}
    </div>
  );
}
