// LoginModal.js
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function LoginModal({ isLoginOpen, setIsLoginOpen }) {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();

    const handleLogin = async () => {
        if (username && password) {
            try {
                // Send POST request to backend with username and password
                const response = await axios.post("http://127.0.0.1:8000/login/", {
                    username,
                    password,
                });

                // Check if the response status is OK (200)
                if (response.status === 200) {
                    alert(`Welcome, ${username}! You are now logged in.`);
                    setIsLoginOpen(false);
                    localStorage.setItem("token", response.data.token); // Assuming the token is in response.data.token
                    navigate("/"); // Redirect to homepage after login
                }
            } catch (error) {
                // Check if the error is a response error (e.g., invalid credentials)
                if (error.response && error.response.status === 400) {
                    setErrorMessage("Invalid credentials. Please try again.");
                } else {
                    // Handle any other errors (network issues, etc.)
                    setErrorMessage("An error occurred. Please try again later.");
                }
            }
        } else {
            setErrorMessage("Please enter both username and password.");
        }
    };

    const handleSignupClick = () => {
        setIsLoginOpen(false);
        navigate("/signup"); // Navigate to signup page
    };

    if (!isLoginOpen) return null; // Don't render if modal is closed

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded mb-3"
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded mb-3"
                />
                {errorMessage && (
                    <p className="text-red-600 text-center mb-3">{errorMessage}</p>
                )}
                <button
                    onClick={handleLogin}
                    className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700"
                >
                    Login
                </button>

                <div className="flex justify-between mt-4">
                    <button
                        className="text-red-600 hover:underline"
                        onClick={handleSignupClick}
                    >
                        Sign Up
                    </button>
                    <button
                        onClick={() => setIsLoginOpen(false)} // close modal when clicking
                        className="text-gray-600 hover:underline"
                    >
                        Continue as Guest
                    </button>
                </div>

                <p className="mt-3 text-center">
                    <a href="/forgot-password" className="text-red-600 hover:underline">
                        Forgot Password?
                    </a>
                </p>
            </div>
        </div>
    );
}
