import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance"; // Ensure axiosInstance is used

export default function LoginModal({ isLoginOpen, setIsLoginOpen, setIsLoggedIn, setUsername }) {
    const [username, setUsernameInput] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();

    const handleLogin = async () => {
        if (username && password) {
            try {
                const response = await axiosInstance.post("login/", {
                    username,
                    password,
                });

                if (response.status === 200) {
                    const { access, refresh } = response.data;

                    // Store tokens in localStorage
                    localStorage.setItem("access_token", access);
                    localStorage.setItem("refresh_token", refresh);

                    // Fetch and store the user's profile
                    fetchUserProfile();

                    alert(`Welcome, ${username}! You are now logged in.`);
                    setIsLoginOpen(false);
                    setIsLoggedIn(true);
                    navigate("/"); // Redirect to homepage after login
                }
            } catch (error) {
                if (error.response) {
                    if (error.response.status === 401) {
                        setErrorMessage("Invalid username or password. Please try again.");
                    } else {
                        setErrorMessage(error.response.data.detail || "An unexpected error occurred.");
                    }
                } else {
                    setErrorMessage("Network error. Please check your connection and try again.");
                }
            }
        } else {
            setErrorMessage("Please enter both username and password.");
        }
    };

    const fetchUserProfile = async () => {
        try {
            const response = await axiosInstance.get("user-profile/");
            const loggedInUsername = response.data.username;

            // ✅ Update HomePage state with the username
            setUsername(loggedInUsername);
            localStorage.setItem("username", loggedInUsername);
        } catch (error) {
            console.error("Error fetching user profile:", error);
        }
    };

    if (!isLoginOpen) return null;

    return (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsernameInput(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded mb-3"
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded mb-3"
                />
                {errorMessage && (
                    <p className="text-red-600 text-center mb-3">{errorMessage}</p>
                )}
                <button
                    onClick={handleLogin}
                    className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700"
                >
                    Login
                </button>

                <div className="flex justify-between mt-4">
                    <button
                        className="text-red-600 hover:underline"
                        onClick={() => {
                            setIsLoginOpen(false);
                            navigate("/signup"); // Navigate to signup page
                        }}
                    >
                        Sign Up
                    </button>
                    <button
                        onClick={() => setIsLoginOpen(false)}
                        className="text-gray-600 hover:underline"
                    >
                        Continue as Guest
                    </button>
                </div>

                <p className="mt-3 text-center">
                    <a href="/forgot-password" className="text-red-600 hover:underline">
                        Forgot Password?
                    </a>
                </p>
            </div>
        </div>
    );
}
