import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance"; // Ensures authentication

export default function Profile() {
    const [userData, setUserData] = useState(null);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axiosInstance.get("user-profile/");
                setUserData(response.data);
            } catch (error) {
                console.error("Error fetching user profile:", error);
                setError("You must be logged in to view this page.");
                setTimeout(() => navigate("/login"), 2000); // Redirect to login after 2 seconds
            }
        };

        fetchUserProfile();
    }, [navigate]);

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
            <div className="bg-white shadow-lg rounded-lg p-8 w-96 text-left"> {/* ✅ Increased width */}
                <h2 className="text-2xl font-bold mb-6">User Profile</h2>

                {error ? (
                    <p className="text-red-600">{error}</p>
                ) : userData ? (
                    <>
                        {/* ✅ Username Field with Edit Button */}
                        <div className="flex justify-between items-center mb-3">
                            <p className="text-lg">
                                <strong>Username:</strong> {userData.username}
                            </p>
                            <button 
                                className="text-blue-600 hover:underline text-sm"
                                onClick={() => alert("Feature to update username coming soon!")}
                            >
                                Edit
                            </button>
                        </div>

                        {/* ✅ Email Field with Edit Button */}
                        <div className="flex justify-between items-center mb-3">
                            <p className="text-lg">
                                <strong>Email:</strong> {userData.email || "Not Available"}
                            </p>
                            <button 
                                className="text-blue-600 hover:underline text-sm"
                                onClick={() => alert("Feature to update email coming soon!")}
                            >
                                Edit
                            </button>
                        </div>

                        {/* ✅ Action Buttons */}
                        <div className="flex justify-between mt-6">
                            <button 
                                onClick={() => navigate("/")} 
                                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                            >
                                Go to Homepage
                            </button>

                            <button 
                                onClick={() => alert("Feature to reset password coming soon!")} 
                                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                            >
                                Reset Password
                            </button>
                        </div>
                    </>
                ) : (
                    <p>Loading profile...</p>
                )}
            </div>
        </div>
    );
}
