import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "../utils/axiosInstance"; // Ensures authentication

export default function Profile() {
    const [userData, setUserData] = useState(null);
    const [error, setError] = useState("");
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axiosInstance.get("user-profile/");
                setUserData(response.data);
            } catch (error) {
                console.error("Error fetching user profile:", error);
                setError("You must be logged in to view this page.");
                setTimeout(() => navigate("/login"), 2000); // Redirect to login after 2 seconds
            }
        };

        fetchUserProfile();
    }, [navigate]);

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
            <div className="bg-white shadow-lg rounded-lg p-6 w-98 text-left">
                <h2 className="text-2xl font-bold mb-4">User Profile</h2>

                {error ? (
                    <p className="text-red-600">{error}</p>
                ) : userData ? (
                    <>
                        <p className="text-lg"><strong>Username:</strong> {userData.username}</p>
                        <p className="text-lg"><strong>Email:</strong> {userData.email || "Not Available"}</p>
                        <button 
                            onClick={() => navigate("/")} 
                            className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                        >
                            Go to Homepage
                        </button>
                    </>
                ) : (
                    <p>Loading profile...</p>
                )}
            </div>
        </div>
    );
}
