import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { useLocation } from "react-router-dom";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

// Registering the necessary components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const API_URL = "http://127.0.0.1:8000/api/resale/";

export default function HDBResaleVisualizer() {
  const location = useLocation();
  const {
    selectedDistricts = [],
    selectedRoomType = "",
    type = "multi-district"
  } = location.state || {};

  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedDistricts.length && selectedRoomType) {
      fetchData("price_trends");
    }
  }, [selectedDistricts, selectedRoomType]);

  const fetchData = (analysisType) => {
    setLoading(true);
    setError(null);
  
    axios
      .get(`${API_URL}resale_analysis/`, {
        params: {
          towns: selectedDistricts,
          room_type: selectedRoomType,
          type: analysisType || "price_trends",
        },
      })
      .then((res) => {
        const data = res.data;
        if (data.error) {
          console.log("Backend Error:", data.error); // Log the error message from backend
          setError(data.error); // Display error in frontend
          return;
        }
  
        // Prepare the chart data
        setChartData({
          labels: [...new Set(data.map((d) => d.year))], // Years as labels
          datasets: selectedDistricts.map((town) => ({
            label: town,
            data: data
              .filter((d) => d.town === town)
              .map((d) => d.resale_price), // Resale price as data points
            borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`, // Random color
            fill: false, // No area fill
            tension: 0.1, // Slight curve to the line
          })),
        });
      })
      .catch(() => {
        setError("Failed to fetch data. Please try again.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "HDB Resale Price Trends",
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Year",
        },
      },
      y: {
        title: {
          display: true,
          text: "Resale Price",
        },
        beginAtZero: true, // Ensures y-axis starts from zero
      },
    },
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold">HDB Resale Price Visualizer</h1>

      {loading && <p className="text-blue-500 mt-4">Loading...</p>}
      {error && <p className="text-red-500 mt-4">{error}</p>}

      {chartData && (
        <div className="mt-6">
          <Line data={chartData} options={chartOptions} />
        </div>
      )}
    </div>
  );
}
