import React from "react";
import { useNavigate } from "react-router-dom";

const RecentComparisons = () => {
    const navigate = useNavigate();
    const storedComparisons = JSON.parse(localStorage.getItem("comparisonHistory")) || [];

    const handleViewComparison = (comparison) => {
        const params = new URLSearchParams({
            districts: comparison.districts.join(","),
            startTime: comparison.startTime,
            endTime: comparison.endTime,
        });

        navigate(`/price-comparison?${params.toString()}`);
    };

    return (
        <div className="max-w-4xl mx-auto mt-10 p-6 bg-white shadow-md rounded-lg">
            <h2 className="text-2xl font-semibold mb-4">üïí Recently Viewed Comparisons</h2>

            {storedComparisons.length > 0 ? (
                <ul className="space-y-3">
                    {storedComparisons.map((comparison, index) => (
                        <li 
                            key={index} 
                            onClick={() => handleViewComparison(comparison)} 
                            className="cursor-pointer p-4 bg-gray-100 rounded-lg hover:bg-gray-200 transition"
                        >
                            üìç **{comparison.districts.join(", ")}** 
                            | ‚è≥ {comparison.startTime} - {comparison.endTime}
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="text-gray-500">No recent comparisons yet.</p>
            )}
        </div>
    );
};

export default RecentComparisons;
