import React, { useState, useEffect, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../homepage/Navbar";
import {
  LineChart,
  Line,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const TABLE_API = "http://127.0.0.1:8000/api/resale/resale_comparison/";
const GRAPH_API = "http://127.0.0.1:8000/api/resale/comparison_graph/";

const PriceComparison = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);

  const districts = searchParams.get("districts")?.split(",") || [];
  const startTime = searchParams.get("startTime") || "Unknown";
  const endTime = searchParams.get("endTime") || "Unknown";
  const startMonthUI = searchParams.get("startMonthUI") || startTime;
  const endMonthUI = searchParams.get("endMonthUI") || endTime;

  const [view, setView] = useState("table");
  const [tableData, setTableData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const hasFetchedRef = useRef(false);

  useEffect(() => {
    if (
      !hasFetchedRef.current &&
      districts.length &&
      startTime !== "Unknown" &&
      endTime !== "Unknown"
    ) {
      fetchData();
      hasFetchedRef.current = true;
    }
  }, [districts, startTime, endTime]);

  const fetchData = async () => {
    setLoading(true);
    setError(null);

    const params = new URLSearchParams();
    districts.forEach((town) => params.append("towns", town));
    params.append("start_year", startTime);
    params.append("end_year", endTime);

    try {
      // Table: overall average price for the period
      const tableRes = await axios.get(`${TABLE_API}?${params.toString()}`);
      const tableFormatted = tableRes.data.map((entry) => ({
        town: entry.town,
        avg_price: Number(entry.avg_price).toFixed(2),
      }));
      setTableData(tableFormatted);

      // Graph: average prices over time (monthly or yearly)
      const graphRes = await axios.get(`${GRAPH_API}?${params.toString()}&interval=year`);
      const grouped = {};

      graphRes.data.forEach(({ date, town, avg_price }) => {
        if (!grouped[date]) grouped[date] = { year: date };
        grouped[date][town] = Number(avg_price);
      });

      const graphFormatted = Object.values(grouped).sort((a, b) => a.year.localeCompare(b.year));
      setGraphData(graphFormatted);
    } catch (err) {
      console.error("Error fetching comparison data:", err);
      setError("Failed to load data.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100 text-red-900">
      <Navbar />

      <div className="flex flex-col items-center mt-6">
        <h1 className="text-4xl font-bold my-6">ðŸ“Š Price Comparison Results</h1>

        <div className="bg-white p-6 shadow-lg rounded-lg w-3/4 mb-6 text-center border border-gray-300">
          <h2 className="text-2xl font-semibold">
            Comparing Prices for:
            <span className="text-red-600 font-bold"> {districts.join(", ")}</span>
          </h2>
          <h3 className="text-lg text-gray-700 mt-2">
            Time Range: <span className="font-semibold">{startMonthUI} to {endMonthUI}</span>
          </h3>
        </div>

        {/* Toggle View */}
        <div className="flex gap-4 mb-6">
          <button
            className={`px-6 py-3 font-bold rounded-lg transition ${
              view === "table" ? "bg-blue-600 text-white shadow-md" : "bg-gray-300 text-black"
            }`}
            onClick={() => setView("table")}
          >
            ðŸ“‹ Table View
          </button>
          <button
            className={`px-6 py-3 font-bold rounded-lg transition ${
              view === "graph" ? "bg-blue-600 text-white shadow-md" : "bg-gray-300 text-black"
            }`}
            onClick={() => setView("graph")}
          >
            ðŸ“ˆ Graph View
          </button>
        </div>

        {/* Table View */}
        {view === "table" && (
          <div className="bg-white p-6 shadow-md rounded-lg w-3/4 border border-gray-300">
            <h2 className="text-xl font-semibold mb-4">ðŸ“Œ Table View</h2>
            {loading ? (
              <p className="text-blue-500">Loading...</p>
            ) : error ? (
              <p className="text-red-500">{error}</p>
            ) : (
              <table className="w-full border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-red-600 text-white">
                    <th className="border px-6 py-3">District</th>
                    <th className="border px-6 py-3">Time Range</th>
                    <th className="border px-6 py-3">Average Price</th>
                  </tr>
                </thead>
                <tbody>
                  {tableData.map((row, index) => (
                    <tr key={index} className="border bg-gray-50 hover:bg-gray-200 transition">
                      <td className="border px-6 py-3 text-center">{row.town}</td>
                      <td className="border px-6 py-3 text-center">
                        {startMonthUI} - {endMonthUI}
                      </td>
                      <td className="border px-6 py-3 text-center">
                        ${Number(row.avg_price).toLocaleString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* Graph View */}
        {view === "graph" && (
          <div className="bg-white p-6 shadow-md rounded-lg w-3/4 border border-gray-300">
            <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Graph View (Avg Price per Year)</h2>
            {loading ? (
              <p className="text-blue-500">Loading chart...</p>
            ) : (
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={graphData} margin={{ top: 20, right: 30, bottom: 5, left: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="year" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  {districts.map((town) => (
                    <Line key={town} type="monotone" dataKey={town} stroke="#8884d8" dot={false} />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            )}
          </div>
        )}

        <button
          onClick={() => navigate("/map-selection")}
          className="mt-6 px-6 py-3 bg-red-600 text-white font-bold rounded-lg shadow-lg hover:bg-red-700 transition-all"
        >
          â¬… Back to Selection
        </button>
      </div>
    </div>
  );
};

export default PriceComparison;
