import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import Navbar from "../homepage/Navbar";

const API_URL = "http://127.0.0.1:8000/api/resale/raw_data_by_town/";

const townOptions = [
  "ANG MO KIO", "BEDOK", "BISHAN", "BUKIT BATOK", "BUKIT MERAH",
  "BUKIT PANJANG", "BUKIT TIMAH", "CENTRAL AREA", "CHOA CHU KANG", "CLEMENTI"
];
const roomTypeOptions = [
  "1 ROOM", "2 ROOM", "3 ROOM", "4 ROOM", "5 ROOM", "EXECUTIVE", "MULTI-GENERATION"
];

const SearchResults = () => {
  const location = useLocation();

  const [district, setDistrict] = useState("");
  const [roomType, setRoomType] = useState("");
  const [sortOrder, setSortOrder] = useState("price_asc");
  const [filteredResults, setFilteredResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = (district, roomType, sortOrder) => {
    if (!district) return;

    setLoading(true);

    const params = new URLSearchParams({ town: district });
    if (roomType) params.set("room_type", roomType);

    axios.get(`${API_URL}?${params.toString()}`)
      .then((res) => {
        let results = res.data;

        results.sort((a, b) => {
          switch (sortOrder) {
            case "price_asc":
              return a.resale_price - b.resale_price;
            case "price_desc":
              return b.resale_price - a.resale_price;
            case "street_asc":
              return a.street_name.localeCompare(b.street_name);
            case "street_desc":
              return b.street_name.localeCompare(a.street_name);
            case "month_asc":
              return new Date(a.month) - new Date(b.month);
            case "month_desc":
              return new Date(b.month) - new Date(a.month);
            default:
              return 0;
          }
        });

        setFilteredResults(results);
      })
      .catch((err) => {
        console.error("❌ Error fetching data:", err);
        setFilteredResults([]);
      })
      .finally(() => setLoading(false));
  };

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const districtParam = params.get("district");
    const roomTypeParam = params.get("roomType") || "";

    setDistrict(districtParam || "");
    setRoomType(roomTypeParam);

    if (districtParam) {
      fetchData(districtParam, roomTypeParam, sortOrder);
    }
  }, [location.search, sortOrder]);

  return (
    <div className="min-h-screen bg-gray-100 text-red-900">
      <Navbar />
      <div className="max-w-5xl mx-auto mt-10 px-6">
        <h1 className="text-4xl font-bold text-center mb-10">🏠 HDB Past Listings</h1>

        <div className="bg-white p-6 shadow-md rounded-lg mb-10">
          <h2 className="text-xl font-semibold mb-3">🔎 Filter Listings</h2>
          <div className="flex flex-wrap gap-4 items-center">
            <select
              value={district}
              onChange={(e) => setDistrict(e.target.value)}
              className="border px-4 py-2 rounded-lg w-64"
            >
              <option value="">Select District</option>
              {townOptions.map(town => <option key={town} value={town}>{town}</option>)}
            </select>

            <select
              value={roomType}
              onChange={(e) => setRoomType(e.target.value)}
              className="border px-4 py-2 rounded-lg w-64"
            >
              <option value="">Select Room Type</option>
              {roomTypeOptions.map(type => <option key={type} value={type}>{type}</option>)}
            </select>

            <select
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value)}
              className="border px-4 py-2 rounded-lg"
            >
              <option value="price_asc">Sort by Price: Low to High</option>
              <option value="price_desc">Sort by Price: High to Low</option>
              <option value="street_asc">Sort by Street: A to Z</option>
              <option value="street_desc">Sort by Street: Z to A</option>
              <option value="month_asc">Sort by Month: Oldest to Newest</option>
              <option value="month_desc">Sort by Month: Newest to Oldest</option>
            </select>

            <button
              onClick={() => fetchData(district, roomType, sortOrder)}
              className="px-6 py-2 bg-red-600 text-white font-bold rounded-lg shadow-lg hover:bg-red-700 transition-all"
            >
              ✅ Apply Filter
            </button>
          </div>
        </div>

        {loading && <p className="text-center text-xl font-semibold">Loading results...</p>}
        {!loading && filteredResults.length === 0 && <p className="text-center">No listings found.</p>}
        {!loading && filteredResults.length > 0 && (
          <table className="table-auto w-full bg-white rounded-lg shadow-md overflow-hidden">
            <thead className="bg-red-200">
              <tr>
                <th className="px-4 py-3">Month</th>
                <th className="px-4 py-3">Block</th>
                <th className="px-4 py-3">Street Name</th>
                <th className="px-4 py-3">Floor Area</th>
                <th className="px-4 py-3">Resale Price</th>
              </tr>
            </thead>
            <tbody>
              {filteredResults.map((item, idx) => (
                <tr key={idx} className="text-center border-b hover:bg-gray-100">
                  <td className="px-4 py-2">{item.month}</td>
                  <td className="px-4 py-2">{item.block}</td>
                  <td className="px-4 py-2">{item.street_name}</td>
                  <td className="px-4 py-2">{item.floor_area_sqm}</td>
                  <td className="px-4 py-2">${item.resale_price.toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default SearchResults;
