import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { useLocation } from "react-router-dom";

const API_URL = "http://127.0.0.1:8000/api/resale/";

export default function HDBResaleVisualizer() {
  const location = useLocation();
  const {
    selectedDistricts = [],
    selectedRoomType = "",
    type = "multi-district"
  } = location.state || {};

  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedDistricts.length && selectedRoomType) {
      fetchData("price_trends");
    }
  }, [selectedDistricts, selectedRoomType]);

  const fetchData = (analysisType) => {
    setLoading(true);
    setError(null);

    axios
      .get(`${API_URL}resale_analysis/`, {
        params: {
          towns: selectedDistricts,
          room_type: selectedRoomType,
          type: analysisType || "price_trends"
        }
      })
      .then((res) => {
        const data = res.data;
        setChartData({
          labels: [...new Set(data.map((d) => d.year))],
          datasets: selectedDistricts.map((town) => ({
            label: town,
            data: data.filter((d) => d.town === town).map((d) => d.resale_price),
            borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
            fill: false
          }))
        });
      })
      .catch(() => {
        setError("Failed to fetch data. Please try again.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold">HDB Resale Price Visualizer</h1>

      {loading && <p className="text-blue-500 mt-4">Loading...</p>}
      {error && <p className="text-red-500 mt-4">{error}</p>}

      {chartData && (
        <div className="mt-6">
          <Line data={chartData} />
        </div>
      )}
    </div>
  );
}
