import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const uniqueDistricts = [
  "ANG MO KIO", "BEDOK", "BISHAN", "BUKIT BATOK", "BUKIT MERAH", "BUKIT PANJANG",
  "BUKIT TIMAH", "CENTRAL AREA", "CHOA CHU KANG", "CLEMENTI", "GEYLANG", "HOUGANG",
  "JURONG EAST", "JURONG WEST", "KALLANG/WHAMPOA", "MARINE PARADE", "PASIR RIS",
  "PUNGGOL", "QUEENSTOWN", "SEMBAWANG", "SENGKANG", "SERANGOON", "TAMPINES",
  "TOA PAYOH", "WOODLANDS", "YISHUN"
];

const uniqueYears = [2017, 2018, 2019, 2020, 2021, 2022, 2023, 2024, 2025];

const SearchSection = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [yearFilter, setYearFilter] = useState("");

  const handleSearch = () => {
    if (!searchTerm && !yearFilter) {
      alert("üö® Please select a district or a year to search.");
      return;
    }

    const params = new URLSearchParams();
    if (searchTerm) params.set("district", searchTerm);
    if (yearFilter) {
      params.set("startYear", yearFilter);
      params.set("endYear", yearFilter);
    }

    navigate(`/search-results?${params.toString()}`);
  };

  return (
    <header className="text-center py-16 bg-gradient-to-b from-white to-red-300">
      <h2 className="text-4xl font-extrabold">Find Past HDB Resale Transactions</h2>
      <p className="mt-3 text-lg">Search by district and/or year to see past resale prices.</p>

      {/* Search Inputs */}
      <div className="mt-6 flex justify-center gap-4">
        <select
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border px-4 py-3 rounded-lg w-96"
        >
          <option value="">Select District</option>
          {uniqueDistricts.map((district) => (
            <option key={district} value={district}>{district}</option>
          ))}
        </select>

        <select
          value={yearFilter}
          onChange={(e) => setYearFilter(e.target.value)}
          className="border px-4 py-3 rounded-lg w-48"
        >
          <option value="">Select Year</option>
          {uniqueYears.map((year) => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>

        <button
          className="bg-red-600 text-white px-8 py-3 rounded-lg hover:bg-red-700 transition"
          onClick={handleSearch}
        >
          üîç Search
        </button>
      </div>
    </header>
  );
};

export default SearchSection;
