import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function RecentComparisons() {
    const [comparisons, setComparisons] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // ‚úÖ Use sessionStorage for guests (resets when browser closes)
        const storedComparisons = JSON.parse(sessionStorage.getItem("recentComparisons")) || [];
        setComparisons(storedComparisons);
    }, []);

    const handleViewComparison = (comparison) => {
        navigate(`/map-selection?location=${comparison.location}&timeRange=${comparison.timeRange}`);
    };

    return (
        <section className="py-12 px-6">
            <h3 className="text-3xl font-extrabold text-center mb-6">üìå Recently Viewed Comparisons</h3>
            {comparisons.length === 0 ? (
                <p className="text-center text-gray-500">No recent comparisons. Start exploring!</p>
            ) : (
                <div className="space-y-4">
                    {comparisons.map((comparison, index) => (
                        <div 
                            key={index} 
                            className="p-4 bg-gray-100 rounded-lg shadow-md cursor-pointer hover:bg-gray-200 transition"
                            onClick={() => handleViewComparison(comparison)}
                        >
                            <h4 className="text-xl font-bold">üìç {comparison.location}</h4>
                            <p className="text-gray-600">üïí Time Range: {comparison.timeRange}</p>
                        </div>
                    ))}
                </div>
            )}
        </section>
    );
}
