import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../homepage/Navbar";
import {
  LineChart,
  Line,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const TABLE_API = "http://127.0.0.1:8000/api/resale/resale_comparison/";
const GRAPH_API = "http://127.0.0.1:8000/api/resale/comparison_graph/";

const PriceComparison = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const searchParams = new URLSearchParams(location.search);

  const districts = searchParams.get("districts")?.split(",") || [];
  const startTime = searchParams.get("startTime") || "Unknown";
  const endTime = searchParams.get("endTime") || "Unknown";
  const startMonthUI = searchParams.get("startMonthUI") || startTime;
  const EndMonthUI = searchParams.get("endMonthUI") || endTime;

  const [view, setView] = useState("table");
  const [tableData, setTableData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    const queryParams = new URLSearchParams();
    districts.forEach((town) => queryParams.append("towns", town));
    queryParams.append("start_year", startTime);
    queryParams.append("end_year", endTime);

    try {
      // ðŸ“Š Table data (average across entire period)
      const tableRes = await axios.get(`${TABLE_API}?${queryParams.toString()}`);
      const tableFormatted = tableRes.data.map((entry) => ({
        town: entry.town,
        avg_price: Number(entry.avg_price).toFixed(2),
      }));
      setTableData(tableFormatted);

      // ðŸ“ˆ Graph data (avg over time)
      queryParams.append("interval", "year");  // or "month"
      const graphRes = await axios.get(`${GRAPH_API}?${queryParams.toString()}`);
      const rawGraphData = graphRes.data;

      // Transform to { date, TAMPINES: 312000, WOODLANDS: 284000, ... }
      const grouped = {};
      rawGraphData.forEach(({ date, town, avg_price }) => {
        if (!grouped[date]) grouped[date] = { date };
        grouped[date][town] = Number(avg_price);
      });

      const finalGraph = Object.values(grouped).sort((a, b) => new Date(a.date) - new Date(b.date));
      setGraphData(finalGraph);

    } catch (err) {
      console.error("âŒ Error:", err);
      setError("Failed to load data.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (districts.length && startTime !== "Unknown" && endTime !== "Unknown") {
      fetchData();
    }
  }, [districts, startTime, endTime]);

  return (
    <div className="min-h-screen bg-gray-100 text-red-900">
      <Navbar />

      <div className="max-w-6xl mx-auto mt-10 px-4">
        <h1 className="text-4xl font-bold text-center mb-6">ðŸ“Š Price Comparison Results</h1>

        <div className="bg-white p-6 shadow-md rounded-lg mb-6 text-center">
          <h2 className="text-2xl font-semibold">
            Comparing: <span className="text-red-600">{districts.join(", ")}</span>
          </h2>
          <p className="text-gray-600 mt-2">
            From <strong>{startMonthUI}</strong> to <strong>{EndMonthUI}</strong>
          </p>
        </div>

        <div className="flex justify-center gap-4 mb-6">
          <button
            className={`px-6 py-3 font-bold rounded-lg ${
              view === "table" ? "bg-blue-600 text-white" : "bg-gray-300"
            }`}
            onClick={() => setView("table")}
          >
            ðŸ“‹ Table View
          </button>
          <button
            className={`px-6 py-3 font-bold rounded-lg ${
              view === "graph" ? "bg-blue-600 text-white" : "bg-gray-300"
            }`}
            onClick={() => setView("graph")}
          >
            ðŸ“ˆ Graph View
          </button>
        </div>

        {/* TABLE VIEW */}
        {view === "table" && (
          <div className="bg-white p-6 shadow-md rounded-lg">
            <h2 className="text-xl font-semibold mb-4">ðŸ“Œ Average Prices</h2>
            {loading ? (
              <p className="text-blue-500">Loading table...</p>
            ) : error ? (
              <p className="text-red-500">{error}</p>
            ) : (
              <table className="w-full border border-gray-300">
                <thead className="bg-red-600 text-white">
                  <tr>
                    <th className="p-3">District</th>
                    <th className="p-3">Time Range</th>
                    <th className="p-3">Average Price</th>
                  </tr>
                </thead>
                <tbody>
                  {tableData.map((row, index) => (
                    <tr key={index} className="bg-gray-50 hover:bg-gray-100 text-center">
                      <td className="p-3">{row.town}</td>
                      <td className="p-3">{startMonthUI} - {EndMonthUI}</td>
                      <td className="p-3">${Number(row.avg_price).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {/* GRAPH VIEW */}
        {view === "graph" && (
          <div className="bg-white p-6 shadow-md rounded-lg">
            <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Trend Over Time</h2>
            {loading ? (
              <p className="text-blue-500">Loading graph...</p>
            ) : (
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={graphData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  {districts.map((town) => (
                    <Line key={town} type="monotone" dataKey={town} stroke="#8884d8" dot={false} />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            )}
          </div>
        )}

        <div className="text-center mt-6">
          <button
            onClick={() => navigate("/map-selection")}
            className="px-6 py-3 bg-red-600 text-white font-bold rounded-lg hover:bg-red-700"
          >
            â¬… Back to Selection
          </button>
        </div>
      </div>
    </div>
  );
};

export default PriceComparison;
