import React, { useState } from "react";
import Navbar from "../homepage/Navbar";
import { Bar, Line } from "react-chartjs-2";
import {
    Chart as ChartJS, 
    CategoryScale, 
    LinearScale, 
    PointElement, 
    LineElement, 
    BarElement, 
    Title, 
    Tooltip, 
    Legend
} from "chart.js";

// ‚úÖ Register the required Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

const mockData = [
    { district: "Woodlands", year: 2010, price: 320000 },
    { district: "Woodlands", year: 2015, price: 380000 },
    { district: "Woodlands", year: 2020, price: 420000 },
    { district: "Bishan", year: 2010, price: 550000 },
    { district: "Bishan", year: 2015, price: 620000 },
    { district: "Bishan", year: 2020, price: 680000 },
    { district: "Tampines", year: 2010, price: 430000 },
    { district: "Tampines", year: 2015, price: 500000 },
    { district: "Tampines", year: 2020, price: 570000 },
];

const Insights = () => {
    const [selectedDistrict, setSelectedDistrict] = useState("Woodlands");
    const [budget, setBudget] = useState("");

    // üìä Filter data for selected district
    const filteredData = mockData.filter((d) => d.district === selectedDistrict);

    const lineChartData = {
        labels: filteredData.map((d) => d.year),
        datasets: [
            {
                label: `Resale Prices in ${selectedDistrict}`,
                data: filteredData.map((d) => d.price),
                borderColor: "rgba(255, 99, 132, 1)",
                backgroundColor: "rgba(255, 99, 132, 0.2)",
                borderWidth: 2,
                pointRadius: 5,
                pointBackgroundColor: "rgba(255, 99, 132, 1)",
            },
        ],
    };

    const lineChartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: { display: true },
            title: { display: true, text: "Resale Price Trends" },
        },
        animation: {
            duration: 1000,  // ‚úÖ Ensures animation runs only once (1 second)
        },
        scales: {
            y: { beginAtZero: false },
            x: { grid: { display: false } },
        },
    };    


    const getAffordableDistricts = () => {
        if (!budget) return [];
        return mockData
            .filter((d) => d.price <= budget)
            .map((d) => `${d.district} (${d.year} - $${d.price.toLocaleString()})`);
    };

    return (
        <div className="min-h-screen bg-gray-100 text-red-900">
            <Navbar />
            <div className="max-w-5xl mx-auto mt-10 px-6">
                <h1 className="text-4xl font-bold text-center mb-6">üìä AI-Powered Insights & Trends</h1>

                {/* üìå Select District Dropdown */}
                <div className="flex justify-center mb-6">
                    <label className="font-semibold mr-2">Choose a District:</label>
                    <select
                        value={selectedDistrict}
                        onChange={(e) => setSelectedDistrict(e.target.value)}
                        className="border px-4 py-2 rounded-lg"
                    >
                        <option value="Woodlands">Woodlands</option>
                        <option value="Bishan">Bishan</option>
                        <option value="Tampines">Tampines</option>
                    </select>
                </div>

                {/* üìä Price Trend Graph */}
                <div className="bg-white p-6 shadow-md rounded-lg">
                    <h2 className="text-xl font-semibold mb-4">üìà Resale Price Trend</h2>
                    <Line data={lineChartData} options={lineChartOptions} />
                </div>

                {/* üí∞ What Can You Afford? */}
                <div className="mt-6 bg-white p-6 shadow-md rounded-lg">
                    <h2 className="text-xl font-semibold mb-4">üí∞ What Can You Afford?</h2>
                    <input
                        type="number"
                        value={budget}
                        onChange={(e) => setBudget(e.target.value)}
                        placeholder="Enter your budget (e.g. 400000)"
                        className="border px-4 py-2 rounded-lg w-full"
                    />
                    <div className="mt-4">
                        {budget && (
                            <div>
                                <h3 className="text-lg font-semibold">Affordable Districts:</h3>
                                <ul className="list-disc ml-6 text-green-600">
                                    {getAffordableDistricts().length > 0
                                        ? getAffordableDistricts().map((district, index) => (
                                              <li key={index}>{district}</li>
                                          ))
                                        : "‚ùå No available districts within this budget."}
                                </ul>
                            </div>
                        )}
                    </div>
                </div>

                {/* üî• AI-Powered Insights */}
                <div className="mt-6 bg-white p-6 shadow-md rounded-lg">
                    <h2 className="text-xl font-semibold mb-4">ü§ñ AI-Powered Insights</h2>
                    <p className="text-gray-700">
                        üìå **Predicted 2025 Price for {selectedDistrict}:**  
                        <span className="text-green-600 font-bold">
                            ${(filteredData[filteredData.length - 1].price * 1.1).toFixed(2)}
                        </span>
                    </p>
                    <p className="text-gray-700 mt-2">
                        üìâ **Best Time to Buy:** **{selectedDistrict}** has shown price increases of  
                        <span className="text-red-600 font-bold"> 10% every 5 years.</span>  
                        Buying **now** may be a good idea before prices increase again!
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Insights;
