import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { useLocation } from "react-router-dom";
import Navbar from "../homepage/Navbar"; // ✅ Make sure this path is correct

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

// Register chart components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const API_URL = "http://127.0.0.1:8000/api/resale/";

export default function HDBResaleVisualizer() {
  const location = useLocation();
  const {
    selectedDistricts = [],
    selectedRoomType = "",
    type = "multi-district"
  } = location.state || {};

  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedDistricts.length && selectedRoomType) {
      fetchData("price_trends");
    }
  }, [selectedDistricts, selectedRoomType]);

  const fetchData = (analysisType) => {
    setLoading(true);
    setError(null);

    const queryParams = new URLSearchParams();
    selectedDistricts.forEach((town) => {
      queryParams.append("towns", town);
    });
    queryParams.append("room_type", selectedRoomType);
    queryParams.append("type", analysisType || "price_trends");

    axios
      .get(`${API_URL}resale_analysis/?${queryParams.toString()}`)
      .then((res) => {
        const data = res.data;
        console.log("API Response:", data);

        if (!data.length) {
          setError("No data found for the selected criteria.");
          return;
        }

        const uniqueYears = [...new Set(data.map((d) => Number(d.year)))].sort();

        const processedData = {
          labels: uniqueYears,
          datasets: selectedDistricts.map((town) => {
            const townData = data
              .filter((d) => d.town.toUpperCase() === town.toUpperCase())
              .sort((a, b) => Number(a.year) - Number(b.year));

            return {
              label: town,
              data: uniqueYears.map((year) => {
                const match = townData.find((d) => Number(d.year) === Number(year));
                return match ? parseFloat(match.resale_price) : null;
              }),
              borderColor: `#${Math.floor(Math.random() * 16777215).toString(16)}`,
              fill: false,
              tension: 0.1,
            };
          }),
        };

        setChartData(processedData);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setError("Failed to fetch data. Please try again.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
        labels:{
          font:{
            size: 18,
            weight:"bold"
          }
        }
      },
      title: {
        display: true,
        text: "HDB Resale Price Trends",
        font:{
          size: 16,
        }
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: "Year",
        },
        type: "category",
      },
      y: {
        title: {
          display: true,
          text: "Resale Price (SGD)",
        },
        beginAtZero: false,
      },
    },
  };

  return (
    <div className="min-h-screen bg-white text-red-900">
      <Navbar />
      <h1 className="text-3xl font-bold text-center mt-6">HDB Resale Price Visualizer</h1>

      <div className="w-5/12 mx-auto mt-6 p-4 bg-white rounded-lg shadow">
        {loading && <p className="text-blue-500 mt-4">Loading...</p>}
        {error && <p className="text-red-500 mt-4">{error}</p>}

        {chartData && (
          <div className="mt-6">
            <Line data={chartData} options={chartOptions} />
          </div>
        )}
      </div>
    </div>
  );
}
