import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import Navbar from "../homepage/Navbar";
import {
    LineChart,
    Line,
    CartesianGrid,
    XAxis,
    YAxis,
    Tooltip,
    Legend,
    ResponsiveContainer,
} from "recharts";

const API_URL = "http://127.0.0.1:8000/api/resale/resale_comparison/";

const PriceComparison = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const searchParams = new URLSearchParams(location.search);

    const districts = searchParams.get("districts")?.split(",") || [];
    const startTime = searchParams.get("startTime") || "Unknown";
    const endTime = searchParams.get("endTime") || "Unknown";
    const startMonthUI = searchParams.get("startMonthUI") || startTime;
    const EndMonthUI = searchParams.get("endMonthUI") || endTime;

    const [view, setView] = useState("table");
    const [tableData, setTableData] = useState([]);
    const [graphData, setGraphData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [hasFetched, setHasFetched] = useState(false);

    useEffect(() => {
        if (!hasFetched && districts.length && startTime !== "Unknown" && endTime !== "Unknown") {
            fetchComparisonData();
            setHasFetched(true);
        }
    }, [districts, startTime, endTime, hasFetched]);

    const fetchComparisonData = async () => {
        setLoading(true);
        setError(null);

        const queryParams = new URLSearchParams();
        districts.forEach((town) => queryParams.append("towns", town));
        queryParams.append("start_year", startTime);
        queryParams.append("end_year", endTime);

        try {
            const res = await axios.get(`${API_URL}?${queryParams.toString()}`);
            const rawData = res.data;

            // Table View: Calculate average resale prices
            const grouped = {};
            rawData.forEach((entry) => {
                const { town, resale_price } = entry;
                if (!grouped[town]) grouped[town] = [];
                grouped[town].push(resale_price);
            });

            const averagedData = Object.entries(grouped).map(([town, prices]) => {
                const avg = prices.reduce((sum, price) => sum + price, 0) / prices.length;
                return { town, avg_price: avg.toFixed(2) };
            });

            setTableData(averagedData);

            // Graph View: Use raw resale prices directly
            // Inside fetchComparisonData â†’ replace only the graphData section

            const yearlyMap = {};

            rawData.forEach(({ town, month, resale_price }) => {
                const yearKey = new Date(month).getFullYear();
                if (!yearlyMap[yearKey]) yearlyMap[yearKey] = { year: yearKey };
                if (!yearlyMap[yearKey][town]) yearlyMap[yearKey][town] = [];
                yearlyMap[yearKey][town].push(resale_price);
            });

            const formattedGraphData = Object.values(yearlyMap).map((record) => {
                const dataPoint = { year: record.year };
                Object.keys(record).forEach((key) => {
                    if (key !== "year") {
                        const avg = record[key].reduce((a, b) => a + b, 0) / record[key].length;
                        dataPoint[key] = avg;
                    }
                });
                return dataPoint;
            }).sort((a, b) => a.year - b.year);

            setGraphData(formattedGraphData);

        } catch (err) {
            console.error("Error fetching comparison data:", err);
            setError("Failed to load data.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gray-100 text-red-900">
            <Navbar />

            <div className="flex flex-col items-center mt-6">
                <h1 className="text-4xl font-bold my-6">ðŸ“Š Price Comparison Results</h1>

                <div className="bg-white p-6 shadow-lg rounded-lg w-3/4 mb-6 text-center border border-gray-300">
                    <h2 className="text-2xl font-semibold">
                        Comparing Prices for:
                        <span className="text-red-600 font-bold"> {districts.join(", ")}</span>
          </h2>
          <h3 className="text-lg text-gray-700 mt-2">
            Time Range: <span className="font-semibold">{startMonthUI} to {EndMonthUI}</span>
          </h3>
        </div>

        <div className="flex gap-4 mb-6">
          <button
            className={`px-6 py-3 font-bold rounded-lg transition ${
              view === "table" ? "bg-blue-600 text-white shadow-md" : "bg-gray-300 text-black"
            }`}
            onClick={() => setView("table")}
          >ðŸ“‹ Table View</button>

          <button
            className={`px-6 py-3 font-bold rounded-lg transition ${
              view === "graph" ? "bg-blue-600 text-white shadow-md" : "bg-gray-300 text-black"
            }`}
            onClick={() => setView("graph")}
          >ðŸ“ˆ Graph View</button>
        </div>

        {view === "table" && (
          <div className="bg-white p-6 shadow-md rounded-lg w-3/4 border border-gray-300">
            <h2 className="text-xl font-semibold mb-4">ðŸ“Œ Table View</h2>
            {loading ? (
              <p className="text-blue-500">Loading...</p>
            ) : error ? (
              <p className="text-red-500">{error}</p>
            ) : (
              <table className="w-full border-collapse border border-gray-300">
                <thead>
                  <tr className="bg-red-600 text-white">
                    <th className="border px-6 py-3">District</th>
                    <th className="border px-6 py-3">Time Range</th>
                    <th className="border px-6 py-3">Average Price</th>
                  </tr>
                </thead>
                <tbody>
                  {tableData.map((row, index) => (
                    <tr key={index} className="border bg-gray-50 hover:bg-gray-200 transition">
                      <td className="border px-6 py-3 text-center">{row.town}</td>
                      <td className="border px-6 py-3 text-center">{startMonthUI} - {EndMonthUI}</td>
                      <td className="border px-6 py-3 text-center">${Number(row.avg_price).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        )}

        {view === "graph" && (
          <div className="bg-white p-6 shadow-md rounded-lg w-3/4 border border-gray-300">
            <h2 className="text-xl font-semibold mb-4">ðŸ“ˆ Graph View</h2>
            {loading ? (
              <p className="text-blue-500">Loading chart...</p>
            ) : (
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={graphData} margin={{ top: 20, right: 30, bottom: 5, left: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  {districts.map((town) => (
                    <Line key={town} type="monotone" dataKey={town} stroke="#8884d8" dot={false} />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            )}
          </div>
        )}

        <button
          onClick={() => navigate("/map-selection")}
          className="mt-6 px-6 py-3 bg-red-600 text-white font-bold rounded-lg shadow-lg hover:bg-red-700 transition-all"
        >â¬… Back to Selection</button>
      </div>
    </div>
  );
};

export default PriceComparison;
