import React, { useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom"; // ✅ Import useNavigate for navigation
import Navbar from "./homepage/Navbar"; // ✅ Import Navbar

const districts = [
    { name: "Jurong East", lat: 1.3331, lng: 103.7420 },
    { name: "Boon Lay", lat: 1.3396, lng: 103.7073 },
    { name: "Bukit Panjang", lat: 1.3770, lng: 103.7621 },
    { name: "Woodlands", lat: 1.4360, lng: 103.7860 },
    { name: "Yishun", lat: 1.4295, lng: 103.8355 },
    { name: "Bishan", lat: 1.3508, lng: 103.8494 },
    { name: "Orchard", lat: 1.3046, lng: 103.8320 },
    { name: "Serangoon", lat: 1.3496, lng: 103.8732 },
    { name: "Paya Lebar", lat: 1.3182, lng: 103.8931 },
    { name: "Tampines", lat: 1.3530, lng: 103.9444 },
    { name: "Bedok", lat: 1.3244, lng: 103.9301 },
    { name: "Pasir Ris", lat: 1.3731, lng: 103.9496 },
    { name: "Chinatown", lat: 1.2842, lng: 103.8443 },
    { name: "HarbourFront", lat: 1.2653, lng: 103.8223 },
    { name: "Caldecott", lat: 1.3392, lng: 103.8392 }
];

const MapComponent = () => {
    const mapRef = useRef(null);
    const navigate = useNavigate(); // ✅ Navigation Hook
    const selectedMarkers = useRef([]);
    const selectionEnabled = useRef(false);
    const markersRef = useRef([]);

    useEffect(() => {
        const script = document.createElement("script");
        script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyCI0zhVegbDZWWzRj4GJMNLWx4Lhzr3urk";
        script.async = true;
        script.defer = true;
        script.onload = () => initializeMap();
        document.body.appendChild(script);
    }, []);

    function initializeMap() {
        const map = new window.google.maps.Map(mapRef.current, {
            center: { lat: 1.3521, lng: 103.8198 },
            zoom: 11
        });

        districts.forEach(district => {
            const marker = new window.google.maps.Marker({
                position: { lat: district.lat, lng: district.lng },
                map,
                title: district.name,
                icon: { url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png" }
            });

            markersRef.current.push(marker);
            marker.addListener("click", () => handleMarkerClick(marker, district));
        });
    }

    function handleMarkerClick(marker, district) {
        if (!selectionEnabled.current) {
            alert("Click 'Compare Prices' first to start selecting districts.");
            return;
        }

        if (selectedMarkers.current.some(sel => sel.name === district.name)) {
            alert(`You have already selected ${district.name}. Choose a different district.`);
            return;
        }

        if (selectedMarkers.current.length >= 5) {
            alert("You can select up to 5 districts only.");
            return;
        }

        selectedMarkers.current.push(district);
        marker.setIcon("http://maps.google.com/mapfiles/ms/icons/blue-dot.png");
    }

    function proceedToNextStep() {
        if (selectionEnabled.current) {
            alert("Selection is already enabled. Choose at least 2 or up to 5 districts.");
            return;
        }

        selectionEnabled.current = true;
        alert("You can now select at least 2 districts and up to 5 districts for comparison!");
    }

    function comparePrices() {
        if (selectedMarkers.current.length < 2 || selectedMarkers.current.length > 5) {
            alert("Please select at least 2 and at most 5 districts before comparing.");
            return;
        }

        const selectedNames = selectedMarkers.current.map(marker => marker.name).join(", ");
        alert(`You have selected: ${selectedNames}. Proceeding to the next step.`);
    }

    function clearSelections() {
        selectedMarkers.current.forEach(selected => {
            const marker = markersRef.current.find(m => m.title === selected.name);
            if (marker) marker.setIcon("http://maps.google.com/mapfiles/ms/icons/red-dot.png");
        });

        selectedMarkers.current.splice(0, selectedMarkers.current.length);
        selectionEnabled.current = false;
        alert("Selections cleared. Click 'Compare Prices' again to start selecting.");
    }

    return (
        <div className="min-h-screen bg-white text-red-900">
            {/* Navbar */}
            <Navbar />

            <div className="container mx-auto px-6 py-10">
                <h1 className="text-3xl font-bold text-center text-red-900 mb-4">HDB Price Comparison Via Google Maps</h1>

                {/* Buttons */}
                <div className="flex justify-center gap-4 mb-6">
                    <button 
                        className="px-6 py-3 bg-blue-600 text-white font-bold rounded-lg shadow-lg hover:bg-blue-700"
                        onClick={proceedToNextStep}
                    >
                        Compare Prices
                    </button>
                    <button 
                        className="px-6 py-3 bg-green-600 text-white font-bold rounded-lg shadow-lg hover:bg-green-700"
                        onClick={comparePrices}
                    >
                        Proceed
                    </button>
                    <button 
                        className="px-6 py-3 bg-red-600 text-white font-bold rounded-lg shadow-lg hover:bg-red-700"
                        onClick={clearSelections}
                    >
                        Clear Selections
                    </button>
                </div>

                {/* Map container */}
                <div className="rounded-lg shadow-lg overflow-hidden border border-gray-300 mb-6">
                    <div ref={mapRef} style={{ width: "100%", height: "600px" }} />
                </div>

                {/* ✅ "Go Back to Homepage" Button */}
                <div className="flex justify-center mt-6">
                    <button 
                        onClick={() => navigate("/")} 
                        className="px-6 py-3 bg-red-600 text-white font-bold rounded-lg shadow-lg hover:bg-red-700 transition-all"
                    >
                        ⬅ Go Back to Homepage
                    </button>
                </div>
            </div>
        </div>
    );
};

export default MapComponent;
